    public class PokemonMove
    {
        public string Name, Type, StrExp;
        public int Category, Power, Accuracy, PP, Critical;
        public bool Direct, Bite, Punch, Pulse, Minimize, Sound, Range, Slice;

        // ↓少なくとも「弾の技」も必要か？（防弾判定しないなら別に要らないけど、たぶん。どうせ貰い火とかも判定してないし）
        //技名, タイプ, 分類, 威力, 命中, PP, 接触判定, 噛みつき技, パンチ技, 急所技, 波動技, 小さくなる, 音の技, 範囲技, 切る技, 説明
        public PokemonMove(string n, string t, int c, int p, int a, int pp,
            bool d, bool b, bool pu, int crt, bool pls, bool mini, bool snd, bool rng, bool sl, string str )
        {
            Name = n; Type = t; StrExp = str;
            Category = c; Power = p; Accuracy = a; PP = pp;
            Direct = d; Bite = b; Punch = pu; Critical = crt; Pulse = pls; Minimize = mini; Sound = snd; Range = rng; Slice = sl;
        }
    }

    public class PokemonMoveManager {
        public List<PokemonMove> PokemonMoves = new List<PokemonMove>();
        public PokemonMove? GetMoveInfo( string name )
        {
            var p = PokemonMoves.Where(x => x.Name == name).ToList();
            if (p.Count > 0)
                return (p[0]);
            else
                return (null);
        }
        private void AddPokemonMove(string n, string t, int c, int p, int a, int pp,
            bool d, bool b, bool pu, int crt, bool pls, bool mini, bool snd, bool rng, bool sl, string str)
        {
            PokemonMoves.Add(new PokemonMove(n, t, c, p, a, pp, d, b, pu, crt, pls, mini, snd, rng, sl, str));
        }
